//-------------------------------------------------------------------------------------
var get_now_time = function() {
    var d = new Date();
    var now_Year    = d.getFullYear();
    var now_Month   = change_num(d.getMonth() + 1);
    var now_Date    = change_num(d.getDate());
    var now_Hours   = change_num(d.getHours());
    var now_Minutes = change_num(d.getMinutes());
    var now_Seconds = change_num(d.getSeconds());
    var now_time    = now_Year + "-" + now_Month + "-" + now_Date + " " + now_Hours + ":" + now_Minutes + ":" + now_Seconds;
    return now_time;
}
//-------------------------------------------------------------------------------------
var change_num = function(num) {
    if (num < 10) {
        num = "0" + num;
        return num;
    }
    else {
        return num;
    }
}
//-------------------------------------------------------------------------------------
exports.get_now_time = get_now_time;